import React from "react";

export const CommentList = ({ comments }) => {
    const renderedComments = comments.map(({ id, content, status }) => {
        let text;

        if (status === 'approved') {
            text = content;
        }

        return <li key={id}>{content}</li>
    })

    return (
        <ul>
            {renderedComments}
        </ul>
    )
}
